function convertSymbolToText(symbol) {
	if (symbol == '<') {
		return 'less than (<)';
	} else if (symbol == '<=') {
		return 'less than or equal (<=)';
	} else if (symbol == '=') {
		return 'equals (=)';
	} else if (symbol == '>=') {
		return 'greater than or equal (>=)';
	} else if (symbol == '>') {
		return 'greater than (>)';
	} else if (symbol == '!=') {
		return 'not equal (!=)';
	} else if (symbol == 'Contains') {
		return 'contains';
	}
}

